#include <stdio.h>
#include <stdlib.h>
#include <stdbool.h>
#include <gmp.h>

int main(int argc,char** argv)
{
	long term = 2;
	mpz_t a;
	mpz_t b;
	mpz_t digits;

	mpz_init(digits);
	mpz_init(a);
	mpz_init(b);

	mpz_set_ui(a,1);
	mpz_set_ui(b,1);
	mpz_set_str(digits,"",10);

	while( mpz_cmp(digits,b)> 0)
	{
		mpz_add(b,a,b);	// b = a + b
		mpz_sub(a,b,a); // a = b - a
		term++;
	}
	printf("\nThe term is %ld\n",term);
//	printf("\nThe fucking number is %llu.\n",digits);

	return 0;

}



	
